frappe.ui.form.on("Driver", "validate", function(frm) {
    if (frm.doc.wb_driver_licence && frm.doc.wb_driver_licence.length < 12) {
        msgprint("Invalid Licence Number. It should be 12 digit format.");
       validated = false;
    }

});

cur_frm.fields_dict['wb_customer'].get_query = function(doc, dt, dn) {
  return {
    filters: [
    ['DocType', 'name', 'in', 'Customer'],
  ]
  }
};


frappe.ui.form.on("Vehicle", {
    validate: function(frm) {
        $.each(frm.doc.wb_vehicle_drivers, function(i, driver) {
          if (driver.wb_driver_license && driver.wb_driver_license.length!=12) {
              driver.wb_driver_license = '';
              msg = "Invalid License Number";
          }
        });

        if(msg) {
     validated = false;
     refresh_field("wb_vehicle_drivers");
     frappe.throw(msg);
  }
    }
})
