cur_frm.set_query("wbw_vehicle", "wbw_customer", function(doc, cdt, cdn)  {
	return{
		filters: [
			['Vehicle', 'wb_customer', '=', doc.customer]
		]
	}
});

cur_frm.set_query("wbw_driver", "wbw_customer", function(doc, cdt, cdn)  {
	return{
		filters: [
			['Driver', 'wb_customer', '=', doc.customer]
		]
	}
});

cur_frm.cscript.custom_refresh = function(doc) {
    // use the __islocal value of doc, to check if the doc is saved or not
    cur_frm.set_df_property("wbw_customer", "read_only", doc.__islocal ? 0 : 1);
}

